import React from "react";
import { Howl } from "howler";

export default function Phonetic(props) {
  let phoneticSound = `${props.phonetic.sound}`;
  function audioSound(props) {
    props.preventDefault(props);
    console.log(props);
    var sound = new Howl({
      src: [{ phoneticSound }],
    });
    return sound.play(props);
  }
  return (
    <div className="Phonetic d-flex flex-direction-row">
      <div className="audio">
        <button
          className="material-symbols-outlined"
          id="playButton"
          onClick={audioSound}
        >
          play_circle
        </button>
      </div>
      {props.phonetic.text}
    </div>
  );
}
