import React, { useState } from "react";
import axios from "axios";
import Results from "./Results";

export default function Dictionary() {
  let [keyword, setKeyword] = useState("");
  let [results, setResaults] = useState({});

  function search(event) {
    event.preventDefault();

    let apiUrl = `https://api.dictionaryapi.dev/api/v2/entries/en/${keyword}`;
    axios.get(apiUrl).then(handleResponse);
  }

  function handleResponse(response) {
    console.log(response.data[0]);
    console.log(response.data[0].meanings[0]);
    console.log(response.data[0].meanings[0].definitions[0]);
    setResaults(response.data[0]);
  }

  function handleKeywordChange(event) {
    console.log(event.target.value);
    setKeyword(event.target.value);
  }

  return (
    <div className="dictionaryContainer">
      <form onSubmit={search}>
        <input
          type="search"
          placeholder="Type a word"
          autoFocus="on"
          onChange={handleKeywordChange}
        />
      </form>
      <Results resaults="results" />
    </div>
  );
}
